   DEFAULT TABLESPACE streams_tbs
   QUOTA UNLIMITED ON streams_tbs;

BEGIN
  DBMS_STREAMS_AUTH.GRANT_ADMIN_PRIVILEGE(
    grantee          => 'strmadmin',
    grant_privileges => TRUE);
END;
